import React from "react";
import styles from "./AboutMe.module.css"
import { motion } from "framer-motion"
import{ faArrowDown } from "@fortawesome/free-solid-svg-icons"
import{ FontAwesomeIcon} from "@fortawesome/react-fontawesome";

function AboutMe (){
return  ( 
    <div className="containter">
        <motion.div className={styles.box}
            animate={{
            x:50, y:100, opacity:1
            }}
            transition={{
            delay:0.2,
            x:{type:"spring", stiffness:20},
            default:{duration:2}
            }}> 
            <h2><FontAwesomeIcon className={styles.icono} icon={faArrowDown}/></h2>
            </motion.div>
    <div className="englishPresentation">
        <p>
            I am currently  living in the UK from 2020 after living in Spain for almost 3 years.
            All my life I have loved the IT world but I never knew what area to work in.
            Thanks to the search I found this wonderful world of the Front-End.
            After studying many hours I am ready for my first experience.
        </p>
    </div>
    <div className="spanishPresentation">
        <p>
            Actualmente estoy viviendo en Uk luego de vivir casi 3 años en España.
            Toda mi vida me ha encantado el mundo IT pero nunca supe en que area trabajarla.
            Gracias a la busqueda encontre este maravilloso mundo del Front-End.
            Luego de estudiar muchas horas estoy listo para mi primera experiencia.

        </p>
    </div>
    </div>
            );
}
export default AboutMe;
