import React from 'react';
import {
  CDBSidebar,
  CDBSidebarContent,
  CDBSidebarFooter,
  CDBSidebarHeader,
  CDBSidebarMenu,
  CDBSidebarMenuItem,
} from 'cdbreact';
import { Link, BrowserRouter as Router } from 'react-router-dom';
//import { Switch } from 'react-router-dom';
import { Route } from 'react-router-dom';
import Projects from './componentes/Projects';
import Beginning from './componentes/Beggining';
import AboutMe from './componentes/AboutMe';
import Skills from './componentes/Skills';
import ContactMe from './componentes/ContactMe';
const Sidebar = () => {
  return ( 
    <Router>
  <div style={{ display: 'flex', height: '100vh', overflow: 'scroll initial' }}>
    
    
  <CDBSidebar textColor="#fff" backgroundColor="#333">
    
       
    <CDBSidebarHeader prefix={<i className="fa fa-bars fa-large"></i>}>
      <a href="/" className="text-decoration-none" style={{ color: 'inherit' }}>
        Lautaro
      </a>
    </CDBSidebarHeader>

    <CDBSidebarContent className="sidebar-content">
      <CDBSidebarMenu>

        <Link  to="/" className="activeClicked">
          <CDBSidebarMenuItem icon="columns">Beginning</CDBSidebarMenuItem>
        </Link>
        <Link  to="/aboutMe" className="activeClicked">
          <CDBSidebarMenuItem icon="table">About Me</CDBSidebarMenuItem>
        </Link>
        <Link  to="/projects" className="activeClicked">
          <CDBSidebarMenuItem icon="user">Projects</CDBSidebarMenuItem>
        </Link>
        <Link  to="/skills" className="activeClicked">
          <CDBSidebarMenuItem icon="chart-line">Skills</CDBSidebarMenuItem>
        </Link>

        <Link  to="/contact"  className="activeClicked">
          <CDBSidebarMenuItem icon="exclamation-circle">Contact Me</CDBSidebarMenuItem>
        </Link>
    <Route exact path="/" component={Beginning}/>
    <Route exact path="/aboutMe" component={AboutMe}/>
    <Route exact path="/projects" component={Projects}/>
    <Route exact path="/contact" component={ContactMe}/>

      </CDBSidebarMenu>
    </CDBSidebarContent>

    <CDBSidebarFooter style={{ textAlign: 'center' }}>
      <div
        style={{
          padding: '20px 5px',
        }}
      >
        Sidebar Footer
      </div>
    </CDBSidebarFooter>
  </CDBSidebar>
 
  
  
</div></Router>)
};

export default Sidebar;
