import React from "react";
import { Link, BrowserRouter as Router } from 'react-router-dom';
//import { Switch } from 'react-router-dom';
import { Route } from 'react-router-dom';
import Projects from './componentes/Projects';
import Beginning from './componentes/Beggining';
import AboutMe from './componentes/AboutMe';
import Skills from './componentes/Skills';
import ContactMe from './componentes/ContactMe';
function NavBar (){
return <div>
        return (
    <Router> {/* Esto lo tenes que poner al principio de todo siempre*/}
      <div>
        <div className="d-flex bd-highlight d-flex justify-content-center"> 
      <div className="d-flex flex-row bd-highlight fs-2 ">
          <nav>
            <ul className= "d-flex flex-row bd-highlight mb-3 text-decoration-none">
              <li className="ps-4  ms-4 bd-highlight ">
                <Link to="/" className="text-decoration-none  text-danger">Peliculas</Link> {/* El to es para saber donde el "switch
                va a hacer el cambio mas abajo."*/}
              </li>
              <li className="ps-4 ms-4 bd-highlight">
                <Link to="/series" className="text-decoration-none  text-danger">Series</Link>
              </li>
              <li className="ps-4  ms-4 bd-highlight">
                <Link to="/seriesPopulares" className="text-decoration-none text-danger">Series Populares</Link>
              </li>
              <li className="ps-4  ms-4 bd-highlight">
                <Link to="/peliculasPopulares" className="text-decoration-none text-danger">Peliculas Populares</Link>
              </li>
              
            </ul>
          </nav>
          </div>
      </div>
    <Switch>{/* el que hace posible el cambio*/}
          <Route path="/series"> {/* con ayuda el switch este te lleva donde hay que redirigir*/}
            <ListaSeries />
          </Route>
          <Route path="/peliculasPopulares">
            <ListaSeries />
          </Route>
          <Route path="/seriesPopulares">
            <Users />
          </Route>
          <Route path="/">
            <Lista />
          </Route>
        </Switch>
        </div>
    </Router>
</div>
}
export default NavBar;
