import React from "react";
import styles from "./AboutMe.module.css"
import { motion } from "framer-motion"
import{ faArrowDown } from "@fortawesome/free-solid-svg-icons"
import{ FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import imagen from "../../images/imagen.jpg";
function AboutMe (){
return  ( 
    <div className={styles.container}>

        <div className={styles.imagenLautaro} >
          <img  src={imagen} /> 
        </div>

        <div>
        <motion.div className={styles.box}
            animate={{
            x:50, y:100, opacity:1
            }}
            transition={{
            delay:0.4,
            x:{type:"spring", stiffness:500},
            default:{duration:4}
            }}> 
            <h2><FontAwesomeIcon className={styles.icono} icon={faArrowDown}/></h2>
            </motion.div>
        </div>

            <div className={styles.containerPresentation}>
                    <p className={styles.englishPresentation}> 
                        My name is Lautaro I am originally from Buenos Aires-Argentina but I am also Italian because of all my grandparents.
                        I am currently  living in the UK from 2020 after living in Spain for almost 3 years.
                        My hobbies are going to the gym 5 days per week, watching a lot of series and sitting for hours programming and discovering new stuff!
                        All my life I have loved the IT world but I never knew what area to work in.
                        Thanks to the search I found this wonderful world of the Front-End where I can be creative and logical at the same time.
                        After studying and practicing many hours I am ready for my first experience.
                    </p>
            </div>

            <div>
            <motion.div className={styles.box}
    animate={{
      scale: [1, 5, 5, 1, 1],
      rotate: [0, 0, 0, 0, 0],
      borderRadius: ["20%", "10%", "10%", "10%", "10%"],
    }}
    transition={{from:5, duration:3,repeat:1}}> 
    <FontAwesomeIcon className={styles.icono1} icon={faHtml5}/>
    <FontAwesomeIcon className={styles.icono2} icon={faCss3}/>
    <FontAwesomeIcon className={styles.icono3} icon={faJs}/>
    <FontAwesomeIcon className={styles.icono4} icon={faReact}/>
    <FontAwesomeIcon className={styles.icono5} icon={faBootstrap}/>
    <FontAwesomeIcon className={styles.icono6} icon={faGithub}/>
    <FontAwesomeIcon className={styles.icono7} icon={faNodeJs}/>
    <FontAwesomeIcon className={styles.icono8} icon={faGit}/>
</motion.div>
            </div>
    </div>
);
}
export default AboutMe;
